N = 9;
lambda = 0;
TrainingData = LargeData;
TrainingError = zeros(1,N);
TestError = zeros(1,N);
Coefficients = zeros(N+1,N);
for i=1:N
    [TrainingError(1,i),TestError(1,i),alpha] = LSR_cdp30(TrainingData,TestData,i,lambda);
    alpha = [zeros(N+1-length(alpha),1);alpha];
    Coefficients(:,i) = alpha;
end

display(TrainingError);
display(TestError);

x_test = TestData(1,:);
y_test = TestData(2,:);
x_train = TrainingData(1,:);
y_train = TrainingData(2,:);

scatter(x_train,y_train,'red');
grid on;
hold on;
scatter(x_test,y_test,'blue');
[~,TrainingME_N] = min(TrainingError);
[~,TestME_N] = min(TestError);
TrainME_alpha = Coefficients(:,TrainingME_N);
if TrainingME_N ~= TestME_N
    TestME_alpha = Coefficients(:,TestME_N);
    
    syms x
    fplot(poly2sym(TrainME_alpha),[0 1])
    fplot(poly2sym(TestME_alpha),[0 1])
    legend('show','Location','best')
    legend('Training Data','Test Data','Least Training Error Graph','Least Test Error Graph');
else
    fplot(poly2sym(TrainME_alpha),[0 1])
    legend('show','Location','best')
    legend('Training Data','Test Data','Least Error Training and Test Graph');
    
end
TrainingCoefficients = vpa(poly2sym(TrainME_alpha),4);
TestCoefficients = vpa(poly2sym(TestME_alpha),4);
title('Task 2: Ordinary Least Squares on Large Data');
xlabel('x');
ylabel('y');
hold off;

display(TrainingCoefficients);
